import { motion, AnimatePresence } from 'framer-motion';
import { useEffect, useState } from 'react';
import { useSelector } from 'react-redux';

const LanternDisplay = () => {
  const [lanterns, setLanterns] = useState([]);
  const [stars, setStars] = useState([]);
  const [selectedWish, setSelectedWish] = useState(null);
  const { wishes } = useSelector((state) => state.wishes);

  useEffect(() => {
    // Generate random lanterns with wishes
    const newLanterns = Array.from({ length: Math.min(15, wishes.length || 15) }, (_, i) => ({
      id: i,
      x: Math.random() * 90 + 5,
      y: Math.random() * 80 + 10,
      size: Math.random() * 25 + 25,
      color: Math.floor(Math.random() * 5) + 1,
      delay: Math.random() * 3,
      wish: wishes[i] || { name: `Dreamer ${i + 1}`, wish: "May peace and happiness fill our hearts" },
    }));

    // Generate random stars
    const newStars = Array.from({ length: 80 }, (_, i) => ({
      id: i,
      x: Math.random() * 100,
      y: Math.random() * 100,
      size: Math.random() * 2 + 1,
      delay: Math.random() * 3,
    }));

    setLanterns(newLanterns);
    setStars(newStars);
  }, [wishes]);

  return (
    <div className="min-h-screen bg-black overflow-hidden relative cursor-crosshair">
      {/* Stars */}
      {stars.map((star) => (
        <motion.div
          key={star.id}
          className="absolute bg-white rounded-full"
          style={{
            left: `${star.x}%`,
            top: `${star.y}%`,
            width: `${star.size}px`,
            height: `${star.size}px`,
          }}
          animate={{
            opacity: [0.2, 1, 0.2],
            scale: [0.5, 1, 0.5],
          }}
          transition={{
            duration: 2 + Math.random(),
            repeat: Infinity,
            delay: star.delay,
            ease: "easeInOut",
          }}
        />
      ))}

      {/* Lanterns */}
      {lanterns.map((lantern) => (
        <motion.div
          key={lantern.id}
          className={`absolute lantern-gradient-${lantern.color} rounded-lg shadow-lg cursor-pointer`}
          style={{
            left: `${lantern.x}%`,
            top: `${lantern.y}%`,
            width: `${lantern.size}px`,
            height: `${lantern.size * 1.3}px`,
          }}
          animate={{
            y: [-8, 8, -8],
            x: [-3, 3, -3],
            rotate: [-1, 1, -1],
          }}
          transition={{
            duration: 4 + Math.random() * 2,
            repeat: Infinity,
            delay: lantern.delay,
            ease: "easeInOut",
          }}
          whileHover={{
            scale: 1.2,
            boxShadow: "0 0 40px rgba(255, 215, 0, 0.9)",
            zIndex: 10,
          }}
          onClick={() => setSelectedWish(lantern.wish)}
        >
          {/* Lantern body */}
          <div className="w-full h-4/5 rounded-lg relative overflow-hidden">
            <div className="absolute inset-0 bg-gradient-to-b from-transparent via-white/30 to-transparent" />
            <div className="absolute top-2 left-1/2 transform -translate-x-1/2 w-1/3 h-1 bg-black/40 rounded" />
            <div className="absolute bottom-2 left-1/2 transform -translate-x-1/2 w-1/3 h-1 bg-black/40 rounded" />
          </div>

          {/* Lantern top */}
          <div className="w-full h-1/5 bg-gradient-to-b from-yellow-600 to-yellow-800 rounded-t-lg" />

          {/* Glow effect */}
          <motion.div
            className="absolute inset-0 rounded-lg"
            animate={{
              boxShadow: [
                "0 0 15px rgba(255, 215, 0, 0.3)",
                "0 0 25px rgba(255, 215, 0, 0.5)",
                "0 0 15px rgba(255, 215, 0, 0.3)",
              ],
            }}
            transition={{
              duration: 3,
              repeat: Infinity,
              ease: "easeInOut",
            }}
          />
        </motion.div>
      ))}

      {/* Floating particles */}
      {Array.from({ length: 30 }).map((_, i) => (
        <motion.div
          key={`particle-${i}`}
          className="absolute w-1 h-1 bg-yellow-200 rounded-full opacity-60"
          style={{
            left: `${Math.random() * 100}%`,
            top: `${Math.random() * 100}%`,
          }}
          animate={{
            y: [-30, -120],
            opacity: [0.6, 0],
            scale: [1, 0.3],
          }}
          transition={{
            duration: 4 + Math.random() * 3,
            repeat: Infinity,
            delay: Math.random() * 4,
            ease: "easeOut",
          }}
        />
      ))}

      {/* Title */}
      <div className="absolute top-10 left-1/2 transform -translate-x-1/2 text-center z-10">
        <motion.h1
          className="text-4xl md:text-6xl font-bold text-yellow-300 mb-4"
          initial={{ opacity: 0, y: -50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 1 }}
        >
          ‚ú® Night of Wishes ‚ú®
        </motion.h1>
        <motion.p
          className="text-lg text-yellow-100"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 1, delay: 0.5 }}
        >
          Click on any lantern to read the wishes floating in the night sky
        </motion.p>
      </div>

      {/* Wish Modal */}
      <AnimatePresence>
        {selectedWish && (
          <motion.div
            className="fixed inset-0 bg-black/80 flex items-center justify-center z-50 p-4"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            onClick={() => setSelectedWish(null)}
          >
            <motion.div
              className="bg-gradient-to-br from-yellow-400 to-orange-500 rounded-2xl p-8 max-w-md w-full text-black shadow-2xl"
              initial={{ scale: 0.5, opacity: 0 }}
              animate={{ scale: 1, opacity: 1 }}
              exit={{ scale: 0.5, opacity: 0 }}
              onClick={(e) => e.stopPropagation()}
            >
              <div className="text-center mb-6">
                <h3 className="text-2xl font-bold mb-2">üèÆ Wish from the Sky üèÆ</h3>
                <p className="text-lg font-semibold">~ {selectedWish.name} ~</p>
              </div>

              <div className="bg-white/20 rounded-lg p-4 mb-6">
                <p className="text-lg leading-relaxed italic">"{selectedWish.wish}"</p>
              </div>

              <button
                onClick={() => setSelectedWish(null)}
                className="w-full bg-black/20 hover:bg-black/30 text-white font-bold py-3 rounded-lg transition-colors"
              >
                Close
              </button>
            </motion.div>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
};

export default LanternDisplay;
