import { motion, AnimatePresence } from 'framer-motion';
import { useEffect, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { ref, onValue, query, orderByChild, limitToLast } from 'firebase/database';
import { database } from '../firebase';
import { setWishes } from '../store/wishesSlice';

const LanternDisplay = () => {
  const [lanterns, setLanterns] = useState([]);
  const [selectedWish, setSelectedWish] = useState(null);
  const { wishes } = useSelector((state) => state.wishes);
  const dispatch = useDispatch();

  useEffect(() => {
    // Listen for latest 50 wishes from Firebase
    const wishesQuery = query(
      ref(database, 'wishes'),
      orderByChild('timestamp'),
      limitToLast(50)
    );

    const unsubscribe = onValue(wishesQuery, (snapshot) => {
      if (snapshot.exists()) {
        const data = snapshot.val();
        const wishesArray = Object.values(data).reverse(); // Latest first
        dispatch(setWishes(wishesArray));
      }
    });

    return () => unsubscribe();
  }, [dispatch]);

  useEffect(() => {
    if (wishes.length > 0) {
      // Generate lanterns in organized rows like the Figma design
      const newLanterns = wishes.slice(0, 50).map((wish, i) => {
        const row = Math.floor(i / 8); // 8 lanterns per row
        const col = i % 8;
        return {
          id: wish.id || i,
          x: 10 + (col * 10), // Evenly spaced horizontally
          y: 15 + (row * 15), // Staggered vertically
          size: 35 + Math.random() * 15, // 35-50px
          color: (i % 5) + 1,
          delay: i * 0.1,
          wish: wish,
        };
      });

      setLanterns(newLanterns);
    }
  }, [wishes]);

  return (
    <div className="min-h-screen bg-gradient-to-b from-indigo-900 via-purple-900 to-black overflow-hidden relative">
      {/* Starry Background */}
      {Array.from({ length: 200 }).map((_, i) => (
        <motion.div
          key={`star-${i}`}
          className="absolute bg-white rounded-full opacity-60"
          style={{
            left: `${Math.random() * 100}%`,
            top: `${Math.random() * 100}%`,
            width: `${Math.random() * 2 + 1}px`,
            height: `${Math.random() * 2 + 1}px`,
          }}
          animate={{
            opacity: [0.3, 1, 0.3],
            scale: [0.5, 1.2, 0.5],
          }}
          transition={{
            duration: 2 + Math.random() * 2,
            repeat: Infinity,
            delay: Math.random() * 3,
            ease: "easeInOut",
          }}
        />
      ))}

      {/* Myanmar Lanterns Grid */}
      {lanterns.map((lantern, index) => (
        <motion.div
          key={lantern.id}
          className="absolute cursor-pointer"
          style={{
            left: `${lantern.x}%`,
            top: `${lantern.y}%`,
          }}
          initial={{ y: 50, opacity: 0, scale: 0 }}
          animate={{ 
            y: 0, 
            opacity: 1, 
            scale: 1,
            rotate: [0, 2, -1, 1, 0],
            x: [0, -5, 0, -8, 0]
          }}
          transition={{
            duration: 6,
            repeat: Infinity,
            delay: lantern.delay,
            ease: "easeInOut",
          }}
          whileHover={{
            scale: 1.3,
            zIndex: 20,
            rotate: 0,
          }}
          onClick={() => setSelectedWish(lantern.wish)}
        >
          {/* Traditional Myanmar Lantern */}
          <div 
            className="relative"
            style={{ width: `${lantern.size}px`, height: `${lantern.size * 1.8}px` }}
          >
            {/* Hanging String */}
            <div className="absolute -top-8 left-1/2 transform -translate-x-1/2 w-0.5 h-8 bg-yellow-600"></div>

            {/* Top Crown */}
            <div className="relative mx-auto mb-2">
              <div 
                className="bg-gradient-to-b from-yellow-400 to-yellow-600 border-2 border-yellow-700 rounded-full shadow-lg"
                style={{ width: `${lantern.size * 0.6}px`, height: `${lantern.size * 0.2}px` }}
              ></div>
              <div 
                className="absolute -top-2 left-1/2 transform -translate-x-1/2 bg-yellow-500 border-2 border-yellow-700 rounded-full"
                style={{ width: `${lantern.size * 0.2}px`, height: `${lantern.size * 0.2}px` }}
              ></div>
            </div>

            {/* Main Lantern Body - Traditional Myanmar Balloon Shape */}
            <motion.div 
              className={`lantern-gradient-${lantern.color} relative shadow-2xl border-2 border-orange-800/60`}
              style={{ 
                width: `${lantern.size}px`, 
                height: `${lantern.size * 1.3}px`,
                borderRadius: `50% 50% 50% 50% / 60% 60% 40% 40%`,
              }}
              animate={{
                boxShadow: [
                  `0 0 ${lantern.size * 0.5}px rgba(255, 215, 0, 0.6)`,
                  `0 0 ${lantern.size * 0.8}px rgba(255, 165, 0, 0.8)`,
                  `0 0 ${lantern.size * 0.5}px rgba(255, 215, 0, 0.6)`
                ]
              }}
              transition={{
                duration: 3,
                repeat: Infinity,
                ease: "easeInOut",
              }}
            >
              {/* Traditional Myanmar Patterns */}
              <div className="absolute inset-2 border-2 border-white/40 rounded-full opacity-80"></div>

              {/* Vertical Bamboo Strips */}
              <div className="absolute left-1/4 top-4 bottom-4 w-1 bg-red-700/50 rounded-full"></div>
              <div className="absolute left-2/4 top-4 bottom-4 w-1 bg-red-700/50 rounded-full"></div>
              <div className="absolute left-3/4 top-4 bottom-4 w-1 bg-red-700/50 rounded-full"></div>

              {/* Horizontal Bands */}
              <div className="absolute top-1/3 left-4 right-4 h-1 bg-red-700/50 rounded-full"></div>
              <div className="absolute top-2/3 left-4 right-4 h-1 bg-red-700/50 rounded-full"></div>

              {/* Inner Paper Glow */}
              <div 
                className="absolute bg-gradient-to-b from-yellow-100/70 via-orange-200/60 to-red-200/50 rounded-full"
                style={{
                  top: `${lantern.size * 0.15}px`,
                  left: `${lantern.size * 0.15}px`,
                  right: `${lantern.size * 0.15}px`,
                  bottom: `${lantern.size * 0.15}px`,
                }}
              ></div>

              {/* Myanmar Script */}
              <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 text-red-800 font-bold opacity-90"
                   style={{ fontSize: `${lantern.size * 0.15}px` }}>
                ·Äù
              </div>
            </motion.div>

            {/* Bottom Tassel Assembly */}
            <div className="relative mx-auto mt-2 flex flex-col items-center">
              {/* Main Tassel Cord */}
              <div 
                className="bg-red-600 border border-red-800 rounded-full"
                style={{ width: '3px', height: `${lantern.size * 0.3}px` }}
              ></div>

              {/* Tassel Ornament */}
              <div 
                className="bg-gradient-to-b from-red-500 to-red-700 border-2 border-red-800 rounded-full shadow-lg"
                style={{ width: `${lantern.size * 0.25}px`, height: `${lantern.size * 0.25}px` }}
              ></div>

              {/* Hanging Threads */}
              <div className="flex gap-1 mt-1">
                {Array.from({ length: 5 }).map((_, i) => (
                  <motion.div
                    key={i}
                    className="bg-red-600 rounded-full"
                    style={{ width: '1px', height: `${4 + Math.random() * 4}px` }}
                    animate={{
                      scaleY: [1, 1.2, 1],
                      opacity: [0.8, 1, 0.8]
                    }}
                    transition={{
                      duration: 2,
                      repeat: Infinity,
                      delay: i * 0.1,
                    }}
                  />
                ))}
              </div>
            </div>
          </div>
        </motion.div>
      ))}

      {/* Floating Light Particles */}
      {Array.from({ length: 40 }).map((_, i) => (
        <motion.div
          key={`particle-${i}`}
          className="absolute w-2 h-2 bg-yellow-300 rounded-full opacity-70"
          style={{
            left: `${Math.random() * 100}%`,
            top: `${Math.random() * 100}%`,
          }}
          animate={{
            y: [-30, -100],
            x: [0, Math.random() * 30 - 15],
            opacity: [0.7, 0],
            scale: [1, 0.3],
          }}
          transition={{
            duration: 4 + Math.random() * 3,
            repeat: Infinity,
            delay: Math.random() * 4,
            ease: "easeOut",
          }}
        />
      ))}

      {/* Wish Modal */}
      <AnimatePresence>
        {selectedWish && (
          <motion.div
            className="fixed inset-0 bg-black/95 flex items-center justify-center z-50 p-4"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            onClick={() => setSelectedWish(null)}
          >
            <motion.div
              className="bg-gradient-to-br from-yellow-300 via-orange-400 to-red-400 rounded-3xl p-10 max-w-lg w-full text-black shadow-2xl border-4 border-yellow-500"
              initial={{ scale: 0.3, opacity: 0, y: 100 }}
              animate={{ scale: 1, opacity: 1, y: 0 }}
              exit={{ scale: 0.3, opacity: 0, y: 100 }}
              onClick={(e) => e.stopPropagation()}
            >
              <div className="text-center mb-8">
                <div className="text-4xl mb-4">üèÆ</div>
                <h3 className="text-3xl font-bold mb-3 text-red-800">Myanmar Lantern Wish</h3>
                <p className="text-xl font-semibold text-orange-900">~ {selectedWish.name} ~</p>
              </div>

              <div className="bg-white/40 rounded-2xl p-6 mb-8 border-2 border-white/60 shadow-inner">
                <p className="text-xl leading-relaxed italic text-red-900 text-center">
                  "{selectedWish.wish}"
                </p>
              </div>

              <button
                onClick={() => setSelectedWish(null)}
                className="w-full bg-red-600 hover:bg-red-700 text-white font-bold py-4 rounded-2xl transition-colors text-lg shadow-lg"
              >
                Close Lantern
              </button>
            </motion.div>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
};

export default LanternDisplay;
