import { motion } from 'framer-motion';
import { useState } from 'react';

const Lantern = ({ wish, index }) => {
  const [isHovered, setIsHovered] = useState(false);

  const gradients = [
    'lantern-gradient-1',
    'lantern-gradient-2', 
    'lantern-gradient-3',
    'lantern-gradient-4',
    'lantern-gradient-5'
  ];

  const gradient = gradients[index % gradients.length];
  const size = Math.random() * 15 + 25; // 25-40px

  return (
    <motion.div
      className="absolute cursor-pointer"
      style={{
        left: `${Math.random() * 90}%`,
        top: `${Math.random() * 70 + 10}%`,
      }}
      initial={{ y: '100vh', scale: 0, opacity: 0 }}
      animate={{ y: 0, scale: 1, opacity: 1 }}
      transition={{ 
        duration: 3,
        delay: index * 0.1,
        ease: "easeOut"
      }}
      whileHover={{ scale: 1.3, zIndex: 10 }}
      onHoverStart={() => setIsHovered(true)}
      onHoverEnd={() => setIsHovered(false)}
    >
      <motion.div
        className="relative"
        animate={{
          y: [0, -8, -3, -12, 0],
          rotate: [0, 1.5, 0, -1.5, 0],
          x: [0, 2, -1, 1, 0]
        }}
        transition={{
          duration: 6 + Math.random() * 2,
          repeat: Infinity,
          ease: "easeInOut"
        }}
      >
        {/* Myanmar Lantern Structure */}
        <div style={{ width: `${size}px`, height: `${size * 1.4}px` }}>
          {/* Top Cap with Ring */}
          <div className="relative mx-auto mb-1">
            <div 
              className="bg-gradient-to-b from-yellow-600 to-yellow-800 rounded-full border border-yellow-900"
              style={{ width: `${size * 0.7}px`, height: `${size * 0.15}px` }}
            ></div>
            <div 
              className="absolute -top-1 left-1/2 transform -translate-x-1/2 w-2 h-2 bg-yellow-700 rounded-full border border-yellow-900"
            ></div>
          </div>

          {/* Main Lantern Body */}
          <motion.div 
            className={`${gradient} relative shadow-lg border border-black/20`}
            style={{ 
              width: `${size}px`, 
              height: `${size * 1.1}px`,
              borderRadius: `${size * 0.15}px`
            }}
            animate={{
              boxShadow: [
                `0 0 ${size * 0.6}px rgba(255, 215, 0, 0.4)`,
                `0 0 ${size * 0.8}px rgba(255, 165, 0, 0.6)`,
                `0 0 ${size * 0.6}px rgba(255, 215, 0, 0.4)`
              ]
            }}
            transition={{
              duration: 3,
              repeat: Infinity,
              ease: "easeInOut"
            }}
          >
            {/* Vertical Bamboo Ribs */}
            <div className="absolute left-1/4 top-0 bottom-0 w-0.5 bg-black/30"></div>
            <div className="absolute left-2/4 top-0 bottom-0 w-0.5 bg-black/30"></div>
            <div className="absolute left-3/4 top-0 bottom-0 w-0.5 bg-black/30"></div>

            {/* Horizontal Bamboo Ribs */}
            <div className="absolute top-1/4 left-0 right-0 h-0.5 bg-black/30"></div>
            <div className="absolute top-2/4 left-0 right-0 h-0.5 bg-black/30"></div>
            <div className="absolute top-3/4 left-0 right-0 h-0.5 bg-black/30"></div>

            {/* Inner Paper Glow */}
            <div 
              className="absolute bg-gradient-to-b from-yellow-200/50 via-orange-200/40 to-red-200/30"
              style={{
                top: `${size * 0.1}px`,
                left: `${size * 0.1}px`,
                right: `${size * 0.1}px`,
                bottom: `${size * 0.1}px`,
                borderRadius: `${size * 0.1}px`
              }}
            ></div>

            {/* Traditional Myanmar Pattern */}
            <div className="absolute inset-2 border border-white/20 rounded opacity-60"></div>
          </motion.div>

          {/* Bottom Tassel */}
          <div className="relative mx-auto mt-1">
            <div 
              className="bg-red-600 rounded-full mx-auto"
              style={{ width: '2px', height: `${size * 0.3}px` }}
            ></div>
            <div 
              className="bg-red-700 rounded-full mx-auto -mt-1"
              style={{ width: `${size * 0.15}px`, height: `${size * 0.15}px` }}
            ></div>
          </div>
        </div>

        {/* Wish Text Tooltip */}
        <motion.div
          className="absolute -top-12 left-1/2 transform -translate-x-1/2 bg-gradient-to-r from-yellow-400 to-orange-500 text-black px-3 py-2 rounded-lg text-xs whitespace-nowrap max-w-64 overflow-hidden text-ellipsis pointer-events-none shadow-lg border border-yellow-600"
          initial={{ opacity: 0, y: 10 }}
          animate={{ opacity: isHovered ? 1 : 0, y: isHovered ? 0 : 10 }}
          transition={{ duration: 0.3 }}
        >
          <div className="font-bold">{wish.name}</div>
          <div className="italic">"{wish.wish}"</div>
        </motion.div>
      </motion.div>
    </motion.div>
  );
};

export default Lantern;
