import { motion, AnimatePresence } from 'framer-motion';
import { useEffect, useState } from 'react';
import { useSelector } from 'react-redux';

const LanternDisplay = () => {
  const [lanterns, setLanterns] = useState([]);
  const [stars, setStars] = useState([]);
  const [selectedWish, setSelectedWish] = useState(null);
  const { wishes } = useSelector((state) => state.wishes);

  useEffect(() => {
    // Generate mixed floating and settled lanterns
    const newLanterns = Array.from({ length: Math.min(20, wishes.length || 20) }, (_, i) => ({
      id: i,
      x: Math.random() * 85 + 5,
      y: Math.random() * 80 + 10,
      size: Math.random() * 20 + 25,
      color: Math.floor(Math.random() * 5) + 1,
      delay: Math.random() * 4,
      floatDelay: Math.random() * 2,
      isFloating: Math.random() > 0.4, // 60% floating, 40% settled
      wish: wishes[i] || { name: `Dreamer ${i + 1}`, wish: "May peace and happiness fill our hearts" },
    }));

    // Generate stars
    const newStars = Array.from({ length: 120 }, (_, i) => ({
      id: i,
      x: Math.random() * 100,
      y: Math.random() * 100,
      size: Math.random() * 2 + 0.5,
      delay: Math.random() * 4,
    }));

    setLanterns(newLanterns);
    setStars(newStars);
  }, [wishes]);

  return (
    <div className="min-h-screen bg-black overflow-hidden relative cursor-crosshair">
      {/* Stars */}
      {stars.map((star) => (
        <motion.div
          key={star.id}
          className="absolute bg-white rounded-full"
          style={{
            left: `${star.x}%`,
            top: `${star.y}%`,
            width: `${star.size}px`,
            height: `${star.size}px`,
          }}
          animate={{
            opacity: [0.1, 0.8, 0.1],
            scale: [0.3, 1, 0.3],
          }}
          transition={{
            duration: 3 + Math.random(),
            repeat: Infinity,
            delay: star.delay,
            ease: "easeInOut",
          }}
        />
      ))}

      {/* Myanmar Festival Lanterns */}
      {lanterns.map((lantern) => (
        <motion.div
          key={lantern.id}
          className="absolute cursor-pointer"
          style={{
            left: `${lantern.x}%`,
            top: `${lantern.y}%`,
          }}
          initial={lantern.isFloating ? { y: 100, opacity: 0 } : { scale: 0, opacity: 0 }}
          animate={lantern.isFloating ? 
            { 
              y: 0, 
              opacity: 1,
              x: [0, 15, -10, 5, 0],
              rotate: [0, 2, -1, 1, 0]
            } : 
            { 
              scale: 1, 
              opacity: 1,
              y: [0, -8, -3, -12, 0],
              rotate: [0, 1.5, 0, -1.5, 0],
              x: [0, 3, -2, 2, 0]
            }
          }
          transition={lantern.isFloating ? 
            {
              duration: 8,
              repeat: Infinity,
              delay: lantern.floatDelay,
              ease: "easeInOut",
            } :
            {
              duration: 6 + Math.random() * 2,
              repeat: Infinity,
              delay: lantern.delay,
              ease: "easeInOut",
            }
          }
          whileHover={{
            scale: 1.2,
            zIndex: 10,
          }}
          onClick={() => setSelectedWish(lantern.wish)}
        >
          {/* Myanmar Festival Lantern Design */}
          <div 
            className="relative"
            style={{ width: `${lantern.size}px`, height: `${lantern.size * 1.6}px` }}
          >
            {/* Top Ornamental Cap */}
            <div className="relative mx-auto mb-1">
              <div 
                className="bg-gradient-to-b from-yellow-500 to-yellow-700 border-2 border-yellow-800 rounded-full"
                style={{ width: `${lantern.size * 0.8}px`, height: `${lantern.size * 0.2}px` }}
              ></div>
              {/* Decorative Ring */}
              <div 
                className="absolute -top-1 left-1/2 transform -translate-x-1/2 bg-yellow-600 border border-yellow-800 rounded-full"
                style={{ width: `${lantern.size * 0.15}px`, height: `${lantern.size * 0.15}px` }}
              ></div>
            </div>

            {/* Main Lantern Body - Traditional Myanmar Shape */}
            <motion.div 
              className={`lantern-gradient-${lantern.color} relative shadow-lg border-2 border-red-800/50`}
              style={{ 
                width: `${lantern.size}px`, 
                height: `${lantern.size * 1.2}px`,
                borderRadius: `${lantern.size * 0.1}px ${lantern.size * 0.1}px ${lantern.size * 0.3}px ${lantern.size * 0.3}px`,
                clipPath: 'polygon(10% 0%, 90% 0%, 95% 100%, 5% 100%)'
              }}
              animate={{
                boxShadow: [
                  `0 0 ${lantern.size * 0.8}px rgba(255, 215, 0, 0.4)`,
                  `0 0 ${lantern.size * 1.2}px rgba(255, 165, 0, 0.7)`,
                  `0 0 ${lantern.size * 0.8}px rgba(255, 215, 0, 0.4)`
                ]
              }}
              transition={{
                duration: 3,
                repeat: Infinity,
                ease: "easeInOut",
              }}
            >
              {/* Traditional Myanmar Patterns */}
              <div className="absolute inset-1 border-2 border-white/30 rounded opacity-70"
                   style={{ borderRadius: `${lantern.size * 0.08}px ${lantern.size * 0.08}px ${lantern.size * 0.25}px ${lantern.size * 0.25}px` }}>
              </div>

              {/* Vertical Decorative Lines */}
              <div className="absolute left-1/4 top-2 bottom-2 w-0.5 bg-red-600/60"></div>
              <div className="absolute left-2/4 top-2 bottom-2 w-0.5 bg-red-600/60"></div>
              <div className="absolute left-3/4 top-2 bottom-2 w-0.5 bg-red-600/60"></div>

              {/* Horizontal Decorative Bands */}
              <div className="absolute top-1/3 left-2 right-2 h-0.5 bg-red-600/60"></div>
              <div className="absolute top-2/3 left-2 right-2 h-0.5 bg-red-600/60"></div>

              {/* Inner Glow Effect */}
              <div 
                className="absolute bg-gradient-to-b from-yellow-200/60 via-orange-300/50 to-red-300/40"
                style={{
                  top: `${lantern.size * 0.1}px`,
                  left: `${lantern.size * 0.1}px`,
                  right: `${lantern.size * 0.1}px`,
                  bottom: `${lantern.size * 0.1}px`,
                  borderRadius: `${lantern.size * 0.05}px ${lantern.size * 0.05}px ${lantern.size * 0.2}px ${lantern.size * 0.2}px`
                }}
              ></div>

              {/* Traditional Myanmar Symbol */}
              <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 text-red-800 font-bold text-xs opacity-80">
                ၀
              </div>
            </motion.div>

            {/* Bottom Decorative Tassel */}
            <div className="relative mx-auto mt-1 flex flex-col items-center">
              <div 
                className="bg-red-600 border border-red-800"
                style={{ width: '2px', height: `${lantern.size * 0.25}px` }}
              ></div>
              <div 
                className="bg-gradient-to-b from-red-600 to-red-800 border border-red-900 rounded-full"
                style={{ width: `${lantern.size * 0.2}px`, height: `${lantern.size * 0.2}px` }}
              ></div>
              {/* Small decorative tassels */}
              <div className="flex gap-1 mt-1">
                <div className="w-0.5 h-2 bg-red-700"></div>
                <div className="w-0.5 h-2 bg-red-700"></div>
                <div className="w-0.5 h-2 bg-red-700"></div>
              </div>
            </div>
          </div>
        </motion.div>
      ))}

      {/* Floating Embers */}
      {Array.from({ length: 30 }).map((_, i) => (
        <motion.div
          key={`ember-${i}`}
          className="absolute w-1 h-1 bg-orange-400 rounded-full opacity-70"
          style={{
            left: `${Math.random() * 100}%`,
            top: `${Math.random() * 100}%`,
          }}
          animate={{
            y: [-40, -150],
            x: [0, Math.random() * 20 - 10],
            opacity: [0.7, 0],
            scale: [1, 0.2],
          }}
          transition={{
            duration: 5 + Math.random() * 3,
            repeat: Infinity,
            delay: Math.random() * 5,
            ease: "easeOut",
          }}
        />
      ))}

      {/* Wish Modal */}
      <AnimatePresence>
        {selectedWish && (
          <motion.div
            className="fixed inset-0 bg-black/90 flex items-center justify-center z-50 p-4"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            onClick={() => setSelectedWish(null)}
          >
            <motion.div
              className="bg-gradient-to-br from-yellow-400 via-orange-500 to-red-500 rounded-2xl p-8 max-w-md w-full text-black shadow-2xl border-2 border-yellow-600"
              initial={{ scale: 0.5, opacity: 0, y: 50 }}
              animate={{ scale: 1, opacity: 1, y: 0 }}
              exit={{ scale: 0.5, opacity: 0, y: 50 }}
              onClick={(e) => e.stopPropagation()}
            >
              <div className="text-center mb-6">
                <h3 className="text-2xl font-bold mb-2">🏮 Myanmar Lantern Wish 🏮</h3>
                <p className="text-lg font-semibold">~ {selectedWish.name} ~</p>
              </div>

              <div className="bg-white/30 rounded-lg p-4 mb-6 border border-white/50">
                <p className="text-lg leading-relaxed italic">"{selectedWish.wish}"</p>
              </div>

              <button
                onClick={() => setSelectedWish(null)}
                className="w-full bg-black/20 hover:bg-black/30 text-white font-bold py-3 rounded-lg transition-colors"
              >
                Close
              </button>
            </motion.div>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
};

export default LanternDisplay;
