import { motion } from 'framer-motion';
import { useEffect, useState } from 'react';

const LanternDisplay = () => {
  const [lanterns, setLanterns] = useState([]);
  const [stars, setStars] = useState([]);

  useEffect(() => {
    // Generate random lanterns
    const newLanterns = Array.from({ length: 15 }, (_, i) => ({
      id: i,
      x: Math.random() * 100,
      y: Math.random() * 100,
      size: Math.random() * 30 + 20,
      color: Math.floor(Math.random() * 5) + 1,
      delay: Math.random() * 3,
    }));

    // Generate random stars
    const newStars = Array.from({ length: 50 }, (_, i) => ({
      id: i,
      x: Math.random() * 100,
      y: Math.random() * 100,
      size: Math.random() * 3 + 1,
      delay: Math.random() * 2,
    }));

    setLanterns(newLanterns);
    setStars(newStars);
  }, []);

  return (
    <div className="min-h-screen bg-gradient-to-b from-indigo-900 via-purple-900 to-black overflow-hidden relative">
      {/* Stars */}
      {stars.map((star) => (
        <motion.div
          key={star.id}
          className="absolute bg-white rounded-full"
          style={{
            left: `${star.x}%`,
            top: `${star.y}%`,
            width: `${star.size}px`,
            height: `${star.size}px`,
          }}
          animate={{
            opacity: [0.3, 1, 0.3],
            scale: [0.8, 1.2, 0.8],
          }}
          transition={{
            duration: 2,
            repeat: Infinity,
            delay: star.delay,
            ease: "easeInOut",
          }}
        />
      ))}

      {/* Lanterns */}
      {lanterns.map((lantern) => (
        <motion.div
          key={lantern.id}
          className={`absolute lantern-gradient-${lantern.color} rounded-lg shadow-lg`}
          style={{
            left: `${lantern.x}%`,
            top: `${lantern.y}%`,
            width: `${lantern.size}px`,
            height: `${lantern.size * 1.3}px`,
          }}
          animate={{
            y: [-10, 10, -10],
            x: [-5, 5, -5],
            rotate: [-2, 2, -2],
          }}
          transition={{
            duration: 4 + Math.random() * 2,
            repeat: Infinity,
            delay: lantern.delay,
            ease: "easeInOut",
          }}
          whileHover={{
            scale: 1.1,
            boxShadow: "0 0 30px rgba(255, 215, 0, 0.8)",
          }}
        >
          {/* Lantern body */}
          <div className="w-full h-4/5 rounded-lg relative overflow-hidden">
            <div className="absolute inset-0 bg-gradient-to-b from-transparent via-white/20 to-transparent" />
            <div className="absolute top-2 left-1/2 transform -translate-x-1/2 w-1/3 h-1 bg-black/30 rounded" />
            <div className="absolute bottom-2 left-1/2 transform -translate-x-1/2 w-1/3 h-1 bg-black/30 rounded" />
          </div>

          {/* Lantern top */}
          <div className="w-full h-1/5 bg-gradient-to-b from-yellow-600 to-yellow-800 rounded-t-lg" />

          {/* Glow effect */}
          <motion.div
            className="absolute inset-0 rounded-lg"
            animate={{
              boxShadow: [
                "0 0 20px rgba(255, 215, 0, 0.4)",
                "0 0 40px rgba(255, 215, 0, 0.6)",
                "0 0 20px rgba(255, 215, 0, 0.4)",
              ],
            }}
            transition={{
              duration: 3,
              repeat: Infinity,
              ease: "easeInOut",
            }}
          />
        </motion.div>
      ))}

      {/* Floating particles */}
      {Array.from({ length: 20 }).map((_, i) => (
        <motion.div
          key={`particle-${i}`}
          className="absolute w-1 h-1 bg-yellow-300 rounded-full"
          style={{
            left: `${Math.random() * 100}%`,
            top: `${Math.random() * 100}%`,
          }}
          animate={{
            y: [-20, -100],
            opacity: [1, 0],
            scale: [1, 0.5],
          }}
          transition={{
            duration: 3 + Math.random() * 2,
            repeat: Infinity,
            delay: Math.random() * 3,
            ease: "easeOut",
          }}
        />
      ))}

      {/* Title */}
      <div className="absolute top-10 left-1/2 transform -translate-x-1/2 text-center z-10">
        <motion.h1
          className="text-4xl md:text-6xl font-bold text-yellow-300 mb-4"
          initial={{ opacity: 0, y: -50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 1 }}
        >
          Lantern Night Sky
        </motion.h1>
        <motion.p
          className="text-lg text-yellow-100"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 1, delay: 0.5 }}
        >
          A magical display of floating lanterns under the starry night
        </motion.p>
      </div>
    </div>
  );
};

export default LanternDisplay;
