import { motion } from 'framer-motion';
import { useState } from 'react';
import { useSelector } from 'react-redux';
import WishForm from './components/WishForm';
import LanternSky from './components/LanternSky';
import LanternDisplay from './components/LanternDisplay';

function App() {
  const [currentPage, setCurrentPage] = useState('main');
  const { totalCount } = useSelector((state) => state.wishes);

  if (currentPage === 'display') {
    return (
      <div className="relative">
        <button
          onClick={() => setCurrentPage('main')}
          className="absolute top-4 left-4 z-20 bg-gradient-to-r from-yellow-600 to-orange-600 hover:from-yellow-700 hover:to-orange-700 text-white px-6 py-3 rounded-full transition-all transform hover:scale-105 shadow-lg"
        >
          ‚Üê Back to Main
        </button>
        <LanternDisplay />
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-900 via-blue-900 to-indigo-900 relative overflow-hidden">
      {/* Navigation and Counter */}
      <div className="absolute top-4 right-4 z-20 flex items-center gap-4">
        <motion.div 
          className="bg-gradient-to-r from-yellow-500 to-orange-500 text-black px-4 py-2 rounded-full font-bold shadow-lg"
          animate={{ scale: [1, 1.05, 1] }}
          transition={{ duration: 2, repeat: Infinity }}
        >
          üèÆ {totalCount || 0} Wishes
        </motion.div>
        <button
          onClick={() => setCurrentPage('display')}
          className="bg-gradient-to-r from-yellow-600 to-orange-600 hover:from-yellow-700 hover:to-orange-700 text-white px-6 py-3 rounded-full transition-all transform hover:scale-105 shadow-lg"
        >
          View Floating Lanterns
        </button>
      </div>

      {/* Header */}
      <motion.header
        className="relative z-10 text-center py-16 px-4"
        initial={{ opacity: 0, y: -50 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 1 }}
      >
        <motion.h1
          className="text-6xl md:text-8xl font-bold mb-6 bg-gradient-to-r from-yellow-300 via-orange-400 to-red-400 bg-clip-text text-transparent"
          animate={{
            textShadow: [
              '0 0 30px rgba(255, 215, 0, 0.6)',
              '0 0 50px rgba(255, 165, 0, 0.8)',
              '0 0 30px rgba(255, 215, 0, 0.6)'
            ]
          }}
          transition={{
            duration: 3,
            repeat: Infinity,
            ease: "easeInOut"
          }}
        >
          üèÆ Thadingyut üèÆ
        </motion.h1>

        <motion.div
          className="text-2xl md:text-3xl text-yellow-100 max-w-4xl mx-auto leading-relaxed mb-8"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 0.5, duration: 1 }}
        >
          <p className="mb-4 font-semibold">Festival of Lights</p>
          <p className="text-lg text-yellow-200/90">
            Send your wishes to the heavens as beautiful Myanmar lanterns float among the stars. 
            Honor the sacred tradition of Thadingyut and let your dreams illuminate the night sky.
          </p>
        </motion.div>
      </motion.header>

      {/* Wish Form */}
      <div className="relative z-10 px-4 mb-16">
        <WishForm />
      </div>

      {/* Lantern Sky Background */}
      <div className="absolute inset-0">
        <LanternSky />
      </div>

      {/* Footer */}
      <motion.footer
        className="relative z-10 text-center py-8 text-yellow-200/80"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 2 }}
      >
        <p className="text-lg">‚ú® May your wishes light up the sky like the sacred lanterns of Thadingyut ‚ú®</p>
      </motion.footer>
    </div>
  );
}

export default App;
