import { motion, AnimatePresence } from 'framer-motion';
import { useEffect, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { ref, onValue, query, orderByChild, limitToLast } from 'firebase/database';
import { database } from '../firebase';
import { setWishes, setTotalCount } from '../store/wishesSlice';

const LanternDisplay = () => {
  const [selectedWish, setSelectedWish] = useState(null);
  const [lanternPositions, setLanternPositions] = useState([]);
  const { wishes, totalCount } = useSelector((state) => state.wishes);
  const dispatch = useDispatch();

  useEffect(() => {
    const wishesQuery = query(
      ref(database, 'wishes'),
      orderByChild('timestamp'),
      limitToLast(50)
    );

    const unsubscribe = onValue(wishesQuery, (snapshot) => {
      if (snapshot.exists()) {
        const data = snapshot.val();
        const wishesArray = Object.values(data).reverse();
        dispatch(setWishes(wishesArray));
      }
    });

    // Get total count
    const countRef = ref(database, 'wishes');
    const countUnsubscribe = onValue(countRef, (snapshot) => {
      if (snapshot.exists()) {
        const data = snapshot.val();
        dispatch(setTotalCount(Object.keys(data).length));
      }
    });

    return () => {
      unsubscribe();
      countUnsubscribe();
    };
  }, [dispatch]);

  // Generate fixed positions only once when wishes first load
  useEffect(() => {
    if (wishes.length > 0 && lanternPositions.length === 0) {
      const positions = wishes.map((_, index) => ({
        x: Math.random() * 90 + 5,
        y: Math.random() * 80 + 10,
        size: Math.random() * 30 + 40, // 40-70px
        rotation: Math.random() * 20 - 10 // -10 to 10 degrees
      }));
      setLanternPositions(positions);
    }
  }, [wishes, lanternPositions.length]);

  return (
    <div className="min-h-screen bg-black overflow-hidden relative">
      {/* Stats Display */}
      <div className="absolute top-4 right-4 bg-black/50 backdrop-blur-sm rounded-lg p-4 text-white z-10">
        <div className="text-sm">
          <div>Total Wishes: <span className="font-bold text-yellow-400">{totalCount.toLocaleString()}</span></div>
          <div>Showing: <span className="font-bold text-blue-400">{wishes.length}</span></div>
        </div>
      </div>

      {/* Sparkling Stars */}
      {Array.from({ length: 100 }).map((_, i) => (
        <motion.div
          key={i}
          className="absolute bg-white rounded-full"
          style={{
            left: `${Math.random() * 100}%`,
            top: `${Math.random() * 100}%`,
            width: `${Math.random() * 3 + 1}px`,
            height: `${Math.random() * 3 + 1}px`,
          }}
          animate={{
            opacity: [0.2, 1, 0.2],
            scale: [0.5, 1.2, 0.5],
          }}
          transition={{
            duration: 2 + Math.random(),
            repeat: Infinity,
            delay: Math.random() * 2,
            ease: "easeInOut",
          }}
        />
      ))}

      {/* Image-based Lanterns */}
      {wishes.map((wish, index) => {
        const position = lanternPositions[index];
        if (!position) return null;

        return (
          <motion.div
            key={wish.id || index}
            className="absolute cursor-pointer"
            style={{
              left: `${position.x}%`,
              top: `${position.y}%`,
            }}
            initial={{ y: 100, opacity: 0, scale: 0 }}
            animate={{ y: 0, opacity: 1, scale: 1 }}
            transition={{
              duration: 2,
              delay: index * 0.1,
              ease: "easeOut"
            }}
            whileHover={{ scale: 1.2 }}
            onClick={() => setSelectedWish(wish)}
          >
            <motion.div
              animate={{
                y: [0, -10, 0],
                rotate: [position.rotation, position.rotation + 2, position.rotation - 2, position.rotation]
              }}
              transition={{
                duration: 4,
                repeat: Infinity,
                ease: "easeInOut"
              }}
            >
              {/* Lantern Image - Replace with your SVG/PNG */}
              <motion.img
                src="/lantern.png" // Place your lantern image in public folder
                alt="Myanmar Lantern"
                style={{
                  width: `${position.size}px`,
                  height: `${position.size * 1.2}px`,
                  filter: 'drop-shadow(0 0 10px rgba(255, 215, 0, 0.6))'
                }}
                animate={{
                  filter: [
                    'drop-shadow(0 0 10px rgba(255, 215, 0, 0.4))',
                    'drop-shadow(0 0 20px rgba(255, 215, 0, 0.8))',
                    'drop-shadow(0 0 10px rgba(255, 215, 0, 0.4))'
                  ]
                }}
                transition={{
                  duration: 3,
                  repeat: Infinity,
                  ease: "easeInOut"
                }}
                onError={(e) => {
                  // Fallback to SVG if image not found
                  e.target.style.display = 'none';
                  e.target.nextSibling.style.display = 'block';
                }}
              />

              {/* Fallback SVG Lantern */}
              <svg
                width={position.size}
                height={position.size * 1.2}
                viewBox="0 0 100 120"
                style={{ display: 'none' }}
                className="drop-shadow-lg"
              >
                {/* Lantern Top */}
                <ellipse cx="50" cy="15" rx="25" ry="8" fill="#FFD700" stroke="#FFA500" strokeWidth="2"/>

                {/* Main Lantern Body */}
                <path
                  d="M25 20 Q25 15 30 15 L70 15 Q75 15 75 20 L75 80 Q75 90 65 95 L35 95 Q25 90 25 80 Z"
                  fill="url(#lanternGradient)"
                  stroke="#FF6B35"
                  strokeWidth="2"
                />

                {/* Lantern Pattern */}
                <rect x="35" y="25" width="30" height="50" fill="none" stroke="#FFF" strokeWidth="1" opacity="0.6" rx="5"/>
                <line x1="40" y1="30" x2="40" y2="70" stroke="#FF4500" strokeWidth="1" opacity="0.8"/>
                <line x1="50" y1="30" x2="50" y2="70" stroke="#FF4500" strokeWidth="1" opacity="0.8"/>
                <line x1="60" y1="30" x2="60" y2="70" stroke="#FF4500" strokeWidth="1" opacity="0.8"/>

                {/* Bottom Tassel */}
                <circle cx="50" cy="100" r="8" fill="#DC143C"/>
                {/* <rect x="48" y="95" width="4" height="15" fill="#8B0000"/> */}

                {/* Gradient Definition */}
                <defs>
                  <linearGradient id="lanternGradient" x1="0%" y1="0%" x2="0%" y2="100%">
                    <stop offset="0%" stopColor="#FFD700"/>
                    <stop offset="50%" stopColor="#FF8C00"/>
                    <stop offset="100%" stopColor="#FF4500"/>
                  </linearGradient>
                </defs>
              </svg>
            </motion.div>
          </motion.div>
        );
      })}

      {/* Wish Modal */}
      <AnimatePresence>
        {selectedWish && (
          <motion.div
            className="fixed inset-0 bg-black/80 flex items-center justify-center z-50 p-4"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            onClick={() => setSelectedWish(null)}
          >
            <motion.div
              className="bg-gradient-to-br from-yellow-400 to-orange-500 rounded-2xl p-6 max-w-md w-full text-black shadow-2xl"
              initial={{ scale: 0.5, opacity: 0 }}
              animate={{ scale: 1, opacity: 1 }}
              exit={{ scale: 0.5, opacity: 0 }}
              onClick={(e) => e.stopPropagation()}
            >
              <div className="text-center mb-4">
                <h3 className="text-xl font-bold mb-2">🏮 Wish 🏮</h3>
                <p className="text-lg font-semibold">~ {selectedWish.name} ~</p>
              </div>

              <div className="bg-white/30 rounded-lg p-4 mb-4">
                <p className="text-lg italic">"{selectedWish.wish}"</p>
              </div>

              <button
                onClick={() => setSelectedWish(null)}
                className="w-full bg-black/20 hover:bg-black/30 text-white font-bold py-2 rounded-lg transition-colors"
              >
                Close
              </button>
            </motion.div>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
};

export default LanternDisplay;
