import { motion, AnimatePresence } from 'framer-motion';
import { useEffect, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { ref, onValue, query, orderByChild, limitToLast } from 'firebase/database';
import { database } from '../firebase';
import { setWishes, setTotalCount } from '../store/wishesSlice';

const LanternDisplay = () => {
  const [selectedWish, setSelectedWish] = useState(null);
  const [lanternPositions, setLanternPositions] = useState([]);
  const { wishes, totalCount } = useSelector((state) => state.wishes);
  const dispatch = useDispatch();

  useEffect(() => {
    const wishesQuery = query(
      ref(database, 'wishes'),
      orderByChild('timestamp'),
      limitToLast(50)
    );

    const unsubscribe = onValue(wishesQuery, (snapshot) => {
      if (snapshot.exists()) {
        const data = snapshot.val();
        const wishesArray = Object.values(data).reverse();
        dispatch(setWishes(wishesArray));
      }
    });

    // Get total count
    const countRef = ref(database, 'wishes');
    const countUnsubscribe = onValue(countRef, (snapshot) => {
      if (snapshot.exists()) {
        const data = snapshot.val();
        dispatch(setTotalCount(Object.keys(data).length));
      }
    });

    return () => {
      unsubscribe();
      countUnsubscribe();
    };
  }, [dispatch]);

  // Generate fixed positions only once when wishes first load
  useEffect(() => {
    if (wishes.length > 0 && lanternPositions.length === 0) {
      const positions = wishes.map((_, index) => ({
        x: Math.random() * 90 + 5,
        y: Math.random() * 80 + 10,
        color: (index % 5) + 1
      }));
      setLanternPositions(positions);
    }
  }, [wishes, lanternPositions.length]);

  return (
    <div className="min-h-screen bg-black overflow-hidden relative">
      {/* Stats Display */}
      <div className="absolute top-4 right-4 bg-black/50 backdrop-blur-sm rounded-lg p-4 text-white z-10">
        <div className="text-sm">
          <div>Total Wishes: <span className="font-bold text-yellow-400">{totalCount.toLocaleString()}</span></div>
        </div>
      </div>

      {/* Sparkling Stars */}
      {Array.from({ length: 100 }).map((_, i) => (
        <motion.div
          key={i}
          className="absolute bg-white rounded-full"
          style={{
            left: `${Math.random() * 100}%`,
            top: `${Math.random() * 100}%`,
            width: `${Math.random() * 3 + 1}px`,
            height: `${Math.random() * 3 + 1}px`,
          }}
          animate={{
            opacity: [0.2, 1, 0.2],
            scale: [0.5, 1.2, 0.5],
          }}
          transition={{
            duration: 2 + Math.random(),
            repeat: Infinity,
            delay: Math.random() * 2,
            ease: "easeInOut",
          }}
        />
      ))}

      {/* Lanterns with Fixed Positions */}
      {wishes.map((wish, index) => {
        const position = lanternPositions[index];
        if (!position) return null;

        return (
          <motion.div
            key={wish.id || index}
            className="absolute cursor-pointer"
            style={{
              left: `${position.x}%`,
              top: `${position.y}%`,
            }}
            initial={{ y: 100, opacity: 0, scale: 0 }}
            animate={{ y: 0, opacity: 1, scale: 1 }}
            transition={{
              duration: 2,
              delay: index * 0.1,
              ease: "easeOut"
            }}
            whileHover={{ scale: 1.2 }}
            onClick={() => setSelectedWish(wish)}
          >
            <motion.div
              animate={{
                y: [0, -10, 0],
                rotate: [0, 2, -2, 0]
              }}
              transition={{
                duration: 4,
                repeat: Infinity,
                ease: "easeInOut"
              }}
            >
              {/* Lantern Top */}
              <div className="w-4 h-2 bg-yellow-600 mx-auto mb-1 rounded"></div>

              {/* Lantern Body */}
              <motion.div 
                className={`w-10 h-14 lantern-gradient-${position.color} rounded-xl relative shadow-lg`}
                animate={{
                  boxShadow: [
                    '0 0 15px rgba(255, 215, 0, 0.4)',
                    '0 0 25px rgba(255, 215, 0, 0.7)',
                    '0 0 15px rgba(255, 215, 0, 0.4)'
                  ]
                }}
                transition={{
                  duration: 3,
                  repeat: Infinity,
                  ease: "easeInOut"
                }}
              >
                <div className="absolute inset-2 border border-white/30 rounded-lg"></div>
              </motion.div>

              {/* Lantern Bottom */}
              <div className="w-6 h-3 bg-yellow-700 mx-auto mt-1 rounded-b-full"></div>
            </motion.div>
          </motion.div>
        );
      })}

      {/* Wish Modal */}
      <AnimatePresence>
        {selectedWish && (
          <motion.div
            className="fixed inset-0 bg-black/80 flex items-center justify-center z-50 p-4"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            onClick={() => setSelectedWish(null)}
          >
            <motion.div
              className="bg-gradient-to-br from-yellow-400 to-orange-500 rounded-2xl p-6 max-w-md w-full text-black shadow-2xl"
              initial={{ scale: 0.5, opacity: 0 }}
              animate={{ scale: 1, opacity: 1 }}
              exit={{ scale: 0.5, opacity: 0 }}
              onClick={(e) => e.stopPropagation()}
            >
              <div className="text-center mb-4">
                <h3 className="text-xl font-bold mb-2">🏮 Wish 🏮</h3>
                <p className="text-lg font-semibold">~ {selectedWish.name} ~</p>
              </div>

              <div className="bg-white/30 rounded-lg p-4 mb-4">
                <p className="text-lg italic">"{selectedWish.wish}"</p>
              </div>

              <button
                onClick={() => setSelectedWish(null)}
                className="w-full bg-black/20 hover:bg-black/30 text-white font-bold py-2 rounded-lg transition-colors"
              >
                Close
              </button>
            </motion.div>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
};

export default LanternDisplay;
