import { motion, AnimatePresence } from 'framer-motion';
import { useEffect, useState } from 'react';
import { useSelector } from 'react-redux';

const LanternDisplay = () => {
  const [lanterns, setLanterns] = useState([]);
  const [stars, setStars] = useState([]);
  const [selectedWish, setSelectedWish] = useState(null);
  const { wishes } = useSelector((state) => state.wishes);

  useEffect(() => {
    // Generate floating lanterns with wishes
    const newLanterns = Array.from({ length: Math.min(12, wishes.length || 12) }, (_, i) => ({
      id: i,
      x: Math.random() * 85 + 5,
      y: Math.random() * 70 + 20,
      size: Math.random() * 20 + 30,
      color: Math.floor(Math.random() * 5) + 1,
      delay: Math.random() * 4,
      floatDelay: Math.random() * 2,
      wish: wishes[i] || { name: `Dreamer ${i + 1}`, wish: "May peace and happiness fill our hearts" },
    }));

    // Generate stars
    const newStars = Array.from({ length: 100 }, (_, i) => ({
      id: i,
      x: Math.random() * 100,
      y: Math.random() * 100,
      size: Math.random() * 2 + 0.5,
      delay: Math.random() * 4,
    }));

    setLanterns(newLanterns);
    setStars(newStars);
  }, [wishes]);

  return (
    <div className="min-h-screen bg-black overflow-hidden relative cursor-crosshair">
      {/* Stars */}
      {stars.map((star) => (
        <motion.div
          key={star.id}
          className="absolute bg-white rounded-full"
          style={{
            left: `${star.x}%`,
            top: `${star.y}%`,
            width: `${star.size}px`,
            height: `${star.size}px`,
          }}
          animate={{
            opacity: [0.1, 0.8, 0.1],
            scale: [0.3, 1, 0.3],
          }}
          transition={{
            duration: 3 + Math.random(),
            repeat: Infinity,
            delay: star.delay,
            ease: "easeInOut",
          }}
        />
      ))}

      {/* Myanmar Lanterns */}
      {lanterns.map((lantern) => (
        <motion.div
          key={lantern.id}
          className="absolute cursor-pointer"
          style={{
            left: `${lantern.x}%`,
            top: `${lantern.y}%`,
          }}
          initial={{ y: 100, opacity: 0 }}
          animate={{ 
            y: 0, 
            opacity: 1,
            x: [0, 15, -10, 5, 0],
            rotate: [0, 2, -1, 1, 0]
          }}
          transition={{
            duration: 8,
            repeat: Infinity,
            delay: lantern.floatDelay,
            ease: "easeInOut",
          }}
          whileHover={{
            scale: 1.15,
            zIndex: 10,
          }}
          onClick={() => setSelectedWish(lantern.wish)}
        >
          {/* Myanmar Lantern Structure */}
          <div 
            className={`relative lantern-gradient-${lantern.color}`}
            style={{ width: `${lantern.size}px`, height: `${lantern.size * 1.4}px` }}
          >
            {/* Top Cap */}
            <div className="absolute -top-2 left-1/2 transform -translate-x-1/2 w-3/4 h-3 bg-gradient-to-b from-yellow-600 to-yellow-800 rounded-full border border-yellow-900"></div>

            {/* Main Body */}
            <div className="w-full h-4/5 rounded-lg relative overflow-hidden border border-black/20">
              {/* Vertical Ribs */}
              <div className="absolute left-1/4 top-0 bottom-0 w-0.5 bg-black/30"></div>
              <div className="absolute left-2/4 top-0 bottom-0 w-0.5 bg-black/30"></div>
              <div className="absolute left-3/4 top-0 bottom-0 w-0.5 bg-black/30"></div>

              {/* Horizontal Ribs */}
              <div className="absolute top-1/4 left-0 right-0 h-0.5 bg-black/30"></div>
              <div className="absolute top-2/4 left-0 right-0 h-0.5 bg-black/30"></div>
              <div className="absolute top-3/4 left-0 right-0 h-0.5 bg-black/30"></div>

              {/* Inner Glow */}
              <div className="absolute inset-2 bg-gradient-to-b from-yellow-200/40 via-orange-200/30 to-red-200/20 rounded"></div>
            </div>

            {/* Bottom Tassel */}
            <div className="absolute -bottom-3 left-1/2 transform -translate-x-1/2">
              <div className="w-1 h-4 bg-red-600 rounded-full"></div>
              <div className="w-2 h-2 bg-red-700 rounded-full -mt-1 -ml-0.5"></div>
            </div>

            {/* Floating Glow Effect */}
            <motion.div
              className="absolute inset-0 rounded-lg pointer-events-none"
              animate={{
                boxShadow: [
                  "0 0 20px rgba(255, 215, 0, 0.4)",
                  "0 0 35px rgba(255, 165, 0, 0.6)",
                  "0 0 20px rgba(255, 215, 0, 0.4)",
                ],
              }}
              transition={{
                duration: 4,
                repeat: Infinity,
                ease: "easeInOut",
              }}
            />
          </div>
        </motion.div>
      ))}

      {/* Floating Embers */}
      {Array.from({ length: 25 }).map((_, i) => (
        <motion.div
          key={`ember-${i}`}
          className="absolute w-1 h-1 bg-orange-400 rounded-full opacity-70"
          style={{
            left: `${Math.random() * 100}%`,
            top: `${Math.random() * 100}%`,
          }}
          animate={{
            y: [-40, -150],
            x: [0, Math.random() * 20 - 10],
            opacity: [0.7, 0],
            scale: [1, 0.2],
          }}
          transition={{
            duration: 5 + Math.random() * 3,
            repeat: Infinity,
            delay: Math.random() * 5,
            ease: "easeOut",
          }}
        />
      ))}

      {/* Wish Modal */}
      <AnimatePresence>
        {selectedWish && (
          <motion.div
            className="fixed inset-0 bg-black/90 flex items-center justify-center z-50 p-4"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            onClick={() => setSelectedWish(null)}
          >
            <motion.div
              className="bg-gradient-to-br from-yellow-400 to-orange-500 rounded-2xl p-8 max-w-md w-full text-black shadow-2xl border-2 border-yellow-600"
              initial={{ scale: 0.5, opacity: 0, y: 50 }}
              animate={{ scale: 1, opacity: 1, y: 0 }}
              exit={{ scale: 0.5, opacity: 0, y: 50 }}
              onClick={(e) => e.stopPropagation()}
            >
              <div className="text-center mb-6">
                <h3 className="text-2xl font-bold mb-2">üèÆ Floating Wish üèÆ</h3>
                <p className="text-lg font-semibold">~ {selectedWish.name} ~</p>
              </div>

              <div className="bg-white/30 rounded-lg p-4 mb-6 border border-white/50">
                <p className="text-lg leading-relaxed italic">"{selectedWish.wish}"</p>
              </div>

              <button
                onClick={() => setSelectedWish(null)}
                className="w-full bg-black/20 hover:bg-black/30 text-white font-bold py-3 rounded-lg transition-colors"
              >
                Close
              </button>
            </motion.div>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
};

export default LanternDisplay;
